import React from 'react'
import { useSpring, animated } from 'react-spring' // Import useSpring and animated
import { Footer } from './components/Footer/Footer'
import { Header } from './components/Header/Header'
import { Main } from './components/Main/Main'
import { GlobalStyle } from './styles/global'
import 'react-toastify/dist/ReactToastify.css'

function App() {
  // Define a spring animation
  const mainSpring = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 }, // Animation duration
  })

  return (
    <>
      <GlobalStyle></GlobalStyle>
      <Header></Header>
      {/* Apply the animated.div wrapper to the Main component */}
      <animated.div style={mainSpring}>
        <Main></Main>
      </animated.div>
      <Footer></Footer>
    </>
  )
}

export default App
