import { useState } from 'react';
import { Container } from "./styles";
import VinayakSingh from "../../assets/yassine.jpg";
import wordpress from "../../assets/wordpress.svg";
import shopify from "../../assets/shopify.svg";
import htmlIcon from "../../assets/html-icon.svg";
import cssIcon from "../../assets/css-icon.svg";
import jsIcon from "../../assets/js-icon.svg";
import nodeIcon from "../../assets/node-icon.svg";
import reactIcon from "../../assets/react-icon.svg";
import typescriptIcon from "../../assets/typescript-icon.svg";
import vueIcon from "../../assets/vue-icon.svg";
import boostrapIcon from "../../assets/bootstrap-icon.svg";
import ScrollAnimation from "react-animate-on-scroll";

export function About() {
  const [hoveredTechnology, setHoveredTechnology] = useState('');

  const handleMouseEnter = (technologyName) => {
    setHoveredTechnology(technologyName);
  };

  const handleMouseLeave = () => {
    setHoveredTechnology('');
  };

  return (
    <Container id="about">
      <div className="about-text">
        <ScrollAnimation animateIn="fadeInLeft">
          <h2>About me</h2>
        </ScrollAnimation>
        <ScrollAnimation animateIn="fadeInLeft" delay={0.1 * 1000}>
          <p>
            Hi there! I'm Yassine, an IT engineer with a passion for creating dynamic online experiences. I leverage powerful frameworks like React and Spring Boot to build modern web applications with a focus on user experience.          
          </p>
        </ScrollAnimation>
        <ScrollAnimation animateIn="fadeInRight" delay={0.2 * 1000} style={{ marginTop: "2rem", marginBottom: "2rem" }}>
          <p>
            For mobile development, I'm proficient in both native and cross-platform approaches. I can develop high-performance Android apps using Java, and I'm comfortable with React Native and Flutter for cross-platform solutions.
          </p>
        </ScrollAnimation>


        <ScrollAnimation animateIn="fadeInRight" delay={0.4 * 1000}>
          <h3>Here are my main skills:</h3>
        </ScrollAnimation>
        <div className="hard-skills">
          <ScrollAnimation animateIn="fadeInUp" delay={0.4 * 1000}>
            <div className="simple-icons--springboot"
                 onMouseEnter={() => handleMouseEnter('Spring Boot')}
                 onMouseLeave={handleMouseLeave}>
            </div>
          </ScrollAnimation>

          <ScrollAnimation animateIn="fadeInUp" delay={0.13 * 1000}>
            <div className="devicon--java"> 
              <img src={javaIcon} alt="Java" 
                   onMouseEnter={() => handleMouseEnter('Java')}
                   onMouseLeave={handleMouseLeave}/>
            </div>
          </ScrollAnimation>

          {/* Other technology logos with hover effect */}
        </div>
      </div>

      <div className="about-image">
        <ScrollAnimation animateIn="fadeInRight" delay={0.20 * 1000}>
          <img src={VinayakSingh} alt="Vinayak Singh" className="round-image" />
        </ScrollAnimation>
      </div>

      {/* Display hovered technology name */}
      {hoveredTechnology && (
        <div className="hovered-technology">{hoveredTechnology}</div>
      )}
    </Container>
  )
}
